     public void AddData(View v) {
            emailStr= em.getText().toString();
             String u=user.getText().toString();
             String p=pass.getText().toString();
            System.out.println("This is"+emailStr);
         ArrayList<HashMap<String, String>> userList1 = data.GetUserByUserId();
         ArrayList<HashMap<String, String>> userList = data.GetUsers();
         System.out.println(String.valueOf(userList));

             if (!ptr.matcher(emailStr).matches())
             {
                 Toast.makeText(getApplicationContext(),"Invalid email",Toast.LENGTH_SHORT).show();
                 flag=false;
             }
             else{
                 flag=true;

             }
              if(String.valueOf(userList).contains(u)||(String.valueOf(userList1).contains(p)))
                {
                  Toast.makeText(getApplicationContext(),"Username or Password is already taken",Toast.LENGTH_SHORT).show();
                    flag=false;
                }


                  if (flag == true) {

                    boolean isInserted = data.insertUserDetails (user.getText().toString(),
                     pass.getText().toString());
                          if (isInserted == true) {
                              user.setText("");
                              em.setText("");
                              pass.setText("");
                              Toast.makeText(Screen1.this, "Data Inserted", Toast.LENGTH_LONG).show();
                              System.out.println("inserted");
                            } else {
                                Toast.makeText(Screen1.this, "Data not Inserted", Toast.LENGTH_LONG).show();
                                System.out.println("not inserted");
                                }
         }
         else{
             Toast.makeText(Screen1.this, "Please insert valid data in all fields", Toast.LENGTH_LONG).show();
         }
     }
     public void openActivity(View v) {
         activity();
     }
     public void activity(){
         ArrayList<HashMap<String, String>> userList1 = data.GetUserByUserId();
         //System.out.println("this is " + userList);
         ArrayList<HashMap<String, String>> userList = data.GetUsers();
         System.out.println("this is " + userList);
         Intent launchIntent = getPackageManager().getLaunchIntentForPackage("com.example.screen2");
         launchIntent.putExtra("data",userList);
         launchIntent.putExtra("p",userList1);
         startActivity(launchIntent);
         overridePendingTransition(R.anim.fadein, R.anim.fadein);
     }

     public void getdata(){
         ArrayList<HashMap<String, String>> userList = data.GetUsers();
         System.out.println("this is " + userList);
     }



